void yuv_filter::thread_ap_clk_no_reset_() {
    if ( ap_rst.read() == ap_const_logic_1) {
        ap_CS_fsm = ap_ST_fsm_state1;
    } else {
        ap_CS_fsm = ap_NS_fsm.read();
    }
    if ( ap_rst.read() == ap_const_logic_1) {
        ap_reg_grp_rgb2yuv_fu_114_ap_start = ap_const_logic_0;
    } else {
        if ((esl_seteq<1,1,1>(ap_const_logic_1, ap_CS_fsm_state1.read()) && 
             esl_seteq<1,1,1>(ap_start.read(), ap_const_logic_1))) {
            ap_reg_grp_rgb2yuv_fu_114_ap_start = ap_const_logic_1;
        } else if (esl_seteq<1,1,1>(ap_const_logic_1, grp_rgb2yuv_fu_114_ap_ready.read())) {
            ap_reg_grp_rgb2yuv_fu_114_ap_start = ap_const_logic_0;
        }
    }
    if ( ap_rst.read() == ap_const_logic_1) {
        ap_reg_grp_yuv2rgb_fu_152_ap_start = ap_const_logic_0;
    } else {
        if (esl_seteq<1,1,1>(ap_const_logic_1, ap_CS_fsm_state5.read())) {
            ap_reg_grp_yuv2rgb_fu_152_ap_start = ap_const_logic_1;
        } else if (esl_seteq<1,1,1>(ap_const_logic_1, grp_yuv2rgb_fu_152_ap_ready.read())) {
            ap_reg_grp_yuv2rgb_fu_152_ap_start = ap_const_logic_0;
        }
    }
    if ( ap_rst.read() == ap_const_logic_1) {
        ap_reg_grp_yuv_scale_fu_134_ap_start = ap_const_logic_0;
    } else {
        if (esl_seteq<1,1,1>(ap_const_logic_1, ap_CS_fsm_state3.read())) {
            ap_reg_grp_yuv_scale_fu_134_ap_start = ap_const_logic_1;
        } else if (esl_seteq<1,1,1>(ap_const_logic_1, grp_yuv_scale_fu_134_ap_ready.read())) {
            ap_reg_grp_yuv_scale_fu_134_ap_start = ap_const_logic_0;
        }
    }
    if ((esl_seteq<1,1,1>(ap_const_logic_1, ap_CS_fsm_state4.read()) && esl_seteq<1,1,1>(grp_yuv_scale_fu_134_ap_done.read(), ap_const_logic_1))) {
        p_scale_height_reg_233 = grp_yuv_scale_fu_134_ap_return_1.read();
        p_scale_width_reg_228 = grp_yuv_scale_fu_134_ap_return_0.read();
    }
    if ((esl_seteq<1,1,1>(ap_const_logic_1, ap_CS_fsm_state2.read()) && esl_seteq<1,1,1>(grp_rgb2yuv_fu_114_ap_done.read(), ap_const_logic_1))) {
        p_yuv_height_reg_208 = grp_rgb2yuv_fu_114_ap_return_1.read();
        p_yuv_width_reg_203 = grp_rgb2yuv_fu_114_ap_return_0.read();
    }
}

